import React, { Component } from 'react';
import Video from '../../components/Video/Video';
import vidSrc from '../../assets/enter-sandman.mp4';
import soundSrc from '../../assets/sound.png';
import './Home.css';

class home extends Component {

    state = {
        jampads: [
            {
                name: 'Bread and Jaam',
                location: 'Bangalore, Bannergatta road',
                price: '500-1000'
            },
            {
                name: 'Astral Studios',
                location: 'Bangalore, HSR Layout',
                price: '800-1500'
            },
            {
                name: 'Boom Box House',
                location: 'Bangalore, Malleshwaram',
                price: '500-1000'
            },
            {
                name: 'Audio Academy',
                location: 'Bangalore, Old madras road',
                price: '500-1000'
            }
        ],
        muted: true
    };

    soundClickHandler = (event, videoTag) => {
        this.setState(prev => {
            return { muted: !prev.muted };
        })
        videoTag.current.muted = this.state.muted;
    }

    render() {
        return (
            <div className='home'>
            <Video isMuted={this.state.muted} click={this.soundClickHandler} src={vidSrc} sound={soundSrc} />
            {/* <JamCards */}
            </div>
        );
    }
}

export default home;
