import React from 'react';
import 'react-html5video/dist/styles.css';
import { DefaultPlayer as Video } from 'react-html5video';
import './Video.css';

const video = (props) => {

    const soundInput = React.createRef();

    return (
        <div className='videoWrap'>
            <img onClick={props.click} ref={soundInput} className='sound' alt='sound Button' src={props.sound}/>
            <video
                className='video'
                autoPlay
                src={props.src}
            />
        </div>)
};

